import React, {useState, useContext} from 'react';
import ProductCard2 from './ProductCard2';
import "./sass/productcard.css";
import MainContext from '../context/Context';
import { useEffect } from 'react';

export default function ProductPanel2() {

    const {products,} = useContext(MainContext);
    const cardList = localStorage.getItem('renderAllCards') === 'y' ? products : products.slice(2,14);
    const [selected, setSelected] = useState(cardList);
    const selectedCards = selected.map(item => {
        return <ProductCard2 
                    key={item.id}
                    id={item.id}
                />
    })

    let filter = JSON.parse(localStorage.getItem('filter')) || {};

    useEffect(() => {
        const btn = document.querySelector("#showAll-btn");
        btn.addEventListener('click', () => {
            btn.innerText = "Fetching Items...";
            btn.style.backgroundColor = "rgb(78, 78, 78)";
            setTimeout(() => {
                setSelected(products);
                btn.style.opacity = 0;
            }, 1000);    
            localStorage.setItem('renderAllCards', 'y')
        })
    })


    return (
        <div className='productPanel2'>
            <div className='productPanel2--grid'>
                {selectedCards}
            </div>
            <button id="showAll-btn" style={{opacity: selected.length < 18 ? 1 : 0}}>Show More Items</button>
        </div>

    )
}
