import React from 'react';
import { Link, useParams } from 'react-router-dom';
import { ArrowLeft } from "@phosphor-icons/react";
import data from "../../data";
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';

import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormLabel from '@mui/material/FormLabel';
import "./sass/productcard.css";


export default function ProductCardDetail() {
    const params = useParams();
    const item = data.filter(item => item.id === Number(params.id))[0];

    // size select
    const [size, setSize] = React.useState('');
    const handleSizeChange = (event) => {
        setSize(event.target.value);
    };
    const sizeItem = item.size.map(val => <MenuItem value={val}>{val} mm</MenuItem>);

    // color select
    const [color, setColor] = React.useState('');
    const handleColorChange = (event) => {
        setColor(event.target.value)
    };
    const colorItem = item.color ? item.color.map(val => <FormControlLabel sx={{color: 'grey.50', '&.Mui-checked': {color: 'grey.50',}}} style={{ backgroundColor: val }} value={val} control={<Radio />} label="" />) : "";


    return (
        <div className='productCardDetail'>
            <Link className="link" to='/shop'><ArrowLeft size={20} />Back to shop</Link>
            <div className='detailPanel'>
                <div className='detailPanel--left'></div>
                <div className='detailPanel--middle'>
                    <img src={`/${item.img}`} alt={item.name} />
                </div>
                <div className='detailPanel--right'>
                    <p>{item.brand}</p>
                    <p>{item.name}</p>
                    <p>{item.desc}</p>
                    <p>Lens Width and Frame Size</p>
                    <FormControl sx={{ m: 1, minWidth: 120 }}>
                        <InputLabel id="select-helper-label">Size</InputLabel>
                        <Select
                            labelId="select-helper-label"
                            id="select-helper"
                            value={size}
                            label="size"
                            onChange={handleSizeChange}
                        >
                            {sizeItem}
                        </Select>
                    </FormControl>



                    <FormControl>
                        <FormLabel id="row-radio-buttons-group-label">Choose Color</FormLabel>
                        <RadioGroup
                            row
                            aria-labelledby="row-radio-buttons-group-label"
                            name="row-radio-buttons-group"
                            value={color}
                            onChange={handleColorChange}
                        >
                            {colorItem}
                        </RadioGroup>
                    </FormControl>

                    <button>Add to Basket</button>


                </div>
            </div>
        </div>
    )
}
