import React, {useState, useContext } from 'react';
import MainContext from '../context/Context';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

import "./sass/filter.css";

export default function Filter() {
    const brands = [
        { label: "All Brands", value: "all brands" },
        { label: "Salt Maalat", value: "salt maalat" },
        { label: "Betsin Maalat", value: "betsin maalat" },
        { label: "Black Kibal", value: "black kibal" },
        { label: "Sexbomb", value: "sexbomb" }
    ];

    const { showFilter, setShowFilter } = useContext(MainContext);

    // brand select
    const [brand, setBrand] = useState('');
    const handleBrandChange = (event) => {
        setBrand(event.target.value);
    };
    const brandItemHTML = brands.map((item, idx) => <MenuItem key={idx} value={item.label}>{item.label}</MenuItem>);

    const applyFilter = () => {
        setShowFilter(false)
    }

    const resetFilter = () => {
        setShowFilter(false)
    }

    return (
        <div className="filter">
            <div className="filter--selector">
                <p className='small-text'>Brand select</p>
                <FormControl sx={{ m: 1, minWidth: 120 }} className="select">
                    <InputLabel id="brand-select-label">Brand</InputLabel>
                    <Select
                        labelId="brand-select-label"
                        id="brand-select"
                        value={brand}
                        label="brand"
                        onChange={handleBrandChange}
                    >
                        {brandItemHTML}
                    </Select>
                </FormControl>
            </div>
            <div className="filter--selector">
                <p>price range</p>
                {/* price bar */}
            </div>
            <div className="filter--btn">
                <button onClick={applyFilter}>Apply filters</button>
                <button onClick={resetFilter}>Reset filters</button>
            </div>
        </div>
    )
}
