import React from 'react';
import "./sass/filter.css";
import {CaretDown} from "@phosphor-icons/react";

export default function Dropdown({ placeholder, options }) {
    const getDisplay = () => {
        return placeholder;
    }

    return (
        <div className="dropdown">
            <div className="dropdown--input">
                <div className="dropdown--selectedValue">{getDisplay()}</div>
                <CaretDown size={16} />
            </div>
            <div className="dropdown--menu">
                {options.map((option) => {
                    return (
                        <div key={option.value} className='dropdown--item'>
                            {option.label}
                        </div>
                    )
                })}
            </div>
        </div>
    )
}
