import React, {useState, useContext} from 'react';
import ProductCard2 from './ProductCard2';
import "./sass/productcard.css";
import MainContext from './component/context/Context';
import { useEffect } from 'react';

export default function ProductPanel2() {

    const {products} = useContext(MainContext);

    const [selected, setSelected] = useState(products.slice(2,14));
    const selectedCards = selected.map(item => {
        return <ProductCard2 
                    key={item.id}
                    id={item.id}
                />
    })

    useEffect(() => {
        const btn = document.querySelector("#showAll-btn");
        btn.addEventListener('click', () => {
            btn.innerText = "Fetching Items...";
            btn.style.backgroundColor = "rgb(78, 78, 78)";
            setTimeout(() => {
                setSelected(products);
                btn.style.opacity = "none";
            }, 1000);    
        })
    })

    // use route guard to show all

    return (
        <div className='productPanel2'>
            <div className='productPanel2--grid'>
                {selectedCards}
            </div>
            <button id="showAll-btn">Show More Items</button>
        </div>

    )
}
