import React, {useState} from 'react';
import ProductCard2 from './ProductCard2';
import "./sass/productcard.css";
import data from "../../data";
import { buttonBaseClasses } from '@mui/material';

export default function ProductPanel2() {
    const [showAll, setShowAll] = useState(false);
    const selected = showAll ? data : data.slice(2,14);
    const selectedCards = selected.map(item => {
        return <ProductCard2 
                    key={item.name}
                    name={item.name}
                    img={item.img}
                    brand={item.brand}
                    price={item.price}
                />
    })

    return (
        <div className='productPanel2'>
            <div className='productPanel2--grid'>
                {selectedCards}
            </div>
            {!showAll && <button >Show More Items</button>}
        </div>



    )
}
