import React from "react";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import {Check} from '@phosphor-icons/react';
import "./sass/productcard.css";

export default function ProductCard2() {
    let added = true;
    const btnText = added ? "Remove from basket" : "Add to basket";
    const btnStyle = {backgroundColor: "white", color: "rgb(242, 242, 242)"};
    // dynamic style

    return (
        <Card className="productCard2" sx={{ maxWidth:200, maxHeight: 240, border: 1, borderColor: 'grey.300', borderRadius: 0 }}>
            {added && <Check className="product--checked" size={20} color="green"/>}
            <CardMedia
                sx={{ height: 110 }}
                image="assets/glasses1.jpg"
                title="glasses"
            />
            {/* change image path later */}
            <CardContent sx={{ height: 130}}>
                <Typography variant="h6" component="div" sx={{fontSize: 13}}>
                    Burnikk
                </Typography>
                <Typography variant="body2" color="grey.400" sx={{fontStyle: 'italic', fontSize: 12}}>
                    Sexbomb
                </Typography>
                <Typography variant="h6" component="div" sx={{fontSize: 15}}>
                    $56.00
                </Typography>
                <button className="hide" style={added && btnStyle}>{btnText}</button>
            </CardContent>   
        </Card>
    )
}
