import React from 'react';
import './sass/cart.css';
import { Plus, Minus, X } from '@phosphor-icons/react';
import data from "../../data";

export default function Cart() {

    const retrieveFromLocalStorage = () => {
        JSON.parse(localStorage.getItem('cart'))
    }

    const products = retrieveFromLocalStorage() || [];
    const [cart, setCart] = (products);


    const handleRemoveProduct = (productId) => {
        setCart((prevCart) => {
            const updatedCart = [];
            prevCart.map(item => {
                if (Number(item.id) !== Number(productId)) {
                    updatedCart.push(item)
                }
            })
            return updatedCart
        })
    }

    const handleUpdateQuantity = (productId, operation) => {
        setCart((prevCart) => {
            const updatedCart = [];
            prevCart.map(item => {
                if (Number(item.id) === Number(productId)) {
                    if (operation === 'add') {
                        item = { ...item, quantity: item.quantity + 1 }
                    } else {
                        item = { ...item, quantity: item.quantity - 1 }
                    }
                }
                updatedCart.push(item)
            })
            return updatedCart
        })
    }



    const cartItemHtml = products.map(product => <div className='cart--item' key={product.id}>
        <div className='cart--item--update'>
            <Plus onClick={handleUpdateQuantity} className='cart--item--icon' size={20} />
            <Minus onClick={handleUpdateQuantity} className='cart--item--icon' size={20} />
        </div>
        <div className='cart--item--content'>
            <img src={product.img} alt={product.name} />
            <h5>{product.name}</h5>
            <h5>{product.price}</h5>
            <div className='content--grid'>
                <p>Quantity</p>
                <p>Size</p>
                <p>Color</p>
                <p>{product.quantity}</p>
                <p>{product.size} mm</p>
                <div className='content--color' style={{ backgroundColor: product.color }}></div>
            </div>
        </div>
        <div className='cart--item--clear'>
            <X onClick={handleRemoveProduct} className='cart--item--icon' size={32} />
        </div>
    </div>)

    // const totalPrice = getProducts().reduce((accumulator, product) => accumulator + (product.price * product.quantity), 0);


    const handleCheckout = () => {
        console.log('checkout');
    }


    return (
        <div className='cart'>
            <div className='cart--title'>
                <h4>My basket</h4>
                <div>
                    <button className='btn-light'>Close</button>
                    <button className='btn-light'>Clear Basket</button>
                </div>
            </div>
            <div className='cart--box'>
                {cartItemHtml}
            </div>
            <hr></hr>
            <div className='cart--checkout'>
                <h4>totalPrice: </h4>
                <button onClick={handleCheckout}>checkout</button>
            </div>

        </div>
    )
}
