import React, { useState } from "react";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { Check } from '@phosphor-icons/react';
import { useNavigate } from "react-router-dom";
import "./sass/productcard.css";
import MainContext from '../context/Context';

export default function ProductCard2({ id, name, img, brand, price}) {
    // get 'products' state and 'local' state
    const { products, local } = useContext(MainContext);

    const item = products.products.filter(item => item.id === Number(id))[0];

    const updateItem = () => {
        let cart = JSON.parse(localStorage.getItem('cart')) || [];
        if (!added) {
            cart.push({id: uuidv4(), name: item.name, price: item.price, img: item.img, quantity: 1, size: item.size[0], color: item.color? item.color[0] : ''})
        } else {
            cart = cart.filter(product => product.id !== item.id)
        }
        localStorage.setItem('cart', JSON.stringify(cart));
        setAdded(prevAdded => !prevAdded);
    }

    const btnText = itemadded ? "Remove from basket" : "Add to basket";
    const btnStyle = added ? { backgroundColor: "rgb(242, 242, 242)", color: "rgb(78, 78, 78)" } : {};

    const navigate = useNavigate();
    const handle = (id) => {
        navigate(`/product/${id}`)
    }

    return (
        <Card className="productCard2"
            sx={{ maxWidth: 200, maxHeight: 240, border: 1, borderColor: added ? 'grey.500' : 'grey.300', borderRadius: 0 }}>
            {added && <Check className="product--checked" size={20} color="green" />}
            <div onClick={() => handle(id)}>
                <CardMedia
                    sx={{ height: 110 }}
                    image={img}
                    title="glasses"
                />
                <CardContent sx={{ height: 130 }}>
                    <Typography variant="h6" component="div" sx={{ fontSize: 13 }}>
                        {name}
                    </Typography>
                    <Typography variant="body2" color="grey.400" sx={{ fontStyle: 'italic', fontSize: 12 }}>
                        {brand}
                    </Typography>
                    <Typography variant="h6" component="div" sx={{ fontSize: 15 }}>
                        ${price}.00
                    </Typography>
                </CardContent>
            </div>
            <button className="hide" style={btnStyle} onClick={updateItem}>{btnText}</button>
        </Card>
    )
}
