import React from "react";
import "./sass/hero.css";
import {ArrowRight} from "@phosphor-icons/react";

export default function Hero(props) {
    const {title, msg, btn} = props;

    return (
        <div className="hero">
            <h1>{title}</h1>
            {msg && <p>{msg}</p>}
            {btn && <button>
                Shop Now
                <ArrowRight size={16} />
            </button>}

        </div>
    )
}
