import React from 'react';
import './sass/cart.css';

export default function Cart() {
    const [cart, setCart] = JSON.parse(localStorage.getItem('cart')) || [];


    const handleRemoveProduct = (productId) => {
        setCart((prevCart) => {
            const updatedCart = [];
            prevCart.map(item => {
                if (Number(item.id) !== Number(productId)) {
                    updatedCart.push(item)
                }
            })
            return updatedCart
        })
    }

    const handleUpdateQuantity = (productId, operation) => {
        setCart((prevCart) => {
            const updatedCart = [];
            prevCart.map(item => {
                if (Number(item.id) === Number(productId)) {
                    if (operation === 'add') {
                        item = { ...item, quantity: item.quantity + 1 }
                    } else {
                        item = { ...item, quantity: item.quantity - 1 }
                    }
                }
                updatedCart.push(item)
            })
            return updatedCart
        })
    }

    const getProducts = () => cart || [];

    // const totalPrice = getProducts().reduce((accumulator, product) => accumulator + (product.price * product.quantity), 0);

    return (
        <div className='cart'>
            <div className=''></div>
