import Header from "./component/header/Header";
import Footer from "./component/footer/Footer";
import Routerview from "./view/Routerview";
import { BrowserRouter } from "react-router-dom";
import "./sass/index.css";
import MainContext from './component/context/Context';
import {useState} from 'react';
import data from "./data";


function App() {
  const dataList = data.map(item => {
      const combo = [];
      item.size.forEach(size => {
        if (item.color) {
          item.color.forEach(color => {
            const key = size + '-' + color;
            combo.push({key: {quantity: 0}});
          })
        } else {
          const key = size + '-';
          combo.push({key: {quantity: 0}});
        }
      })
      return {...item, combo: combo, isAdded: false}
  })


  const [products, setProducts] = useState(dataList);
  const [local, setLocal] = useState(false);

  return (
    <MainContext.Provider value={{local: {local, setLocal}}}>
      <div className="app">
        <BrowserRouter>
          <Header />
          <Routerview />
          <Footer />
        </BrowserRouter>
      </div>
    </MainContext.Provider>
  );
}

export default App;
