import Header from "./component/header/Header";
import Footer from "./component/footer/Footer";
import Routerview from "./view/Routerview";
import { BrowserRouter } from "react-router-dom";
import "./sass/index.css";
import MainContext from './component/context/Context';
import {useState} from 'react';
import data from "./data";


function App() {
  const dataList = data.map(item => {
      return {...item, isAdded: false}
  })

  const [products, setProducts] = useState(dataList);
  const [local, setLocal] = useState(false);
  const [showCart, setShowCart] = useState(false);
  const [cart, setCart] = useState([]);

  return (
    <MainContext.Provider value={{products, setProducts, local, setLocal, showCart, setShowCart, cart, setCart}}>
      <div className="app">
        <BrowserRouter>
          <Header />
          <Routerview />
          <Footer />
        </BrowserRouter>
      </div>
    </MainContext.Provider>
  );
}

export default App;
