import Header from "./component/header/Header";
import Footer from "./component/footer/Footer";
import Routerview from "./view/Routerview";
import { BrowserRouter } from "react-router-dom";
import "./sass/index.css";
import MainContext from './component/context/Context';
import {useState} from 'react';
import data from "./data";


function App() {
  const dataList = data.map(item => {
      return {...item, isAdded: false}
  })

  const [products, setProducts] = useState(dataList);
  const [cartLocal, setCartLocal] = useState(false);
  const [showCart, setShowCart] = useState(false);
  const [cart, setCart] = useState([]);
  const [filterLocal, setFilterLocal] = useState(false);
  const [showFilter, setShowFilter] = useState(false);


      // // initialize price range values
      // const prices = [... new Set(products.map(product => product.price))];
      // const prange = [Math.min(...prices), Math.max(...prices)];
      // const filter = {'brand': 'All Brands', 'sort': 'None', 'priceRange': prange};
      // localStorage.setItem('filter', JSON.stringify(filter));

  return (
    <MainContext.Provider value={{products, setProducts, 
            cartLocal, setCartLocal, showCart, setShowCart, cart, setCart, 
            filterLocal, setFilterLocal, showFilter, setShowFilter}}>
      <div className="app">
        <BrowserRouter>
          <Header />
          <Routerview />
          <Footer />
        </BrowserRouter>
      </div>
    </MainContext.Provider>
  );
}

export default App;
