import React, {useState} from 'react';
import ProductCard2 from './ProductCard2';
import "./sass/productcard.css";
import data from "../../data";
import { buttonBaseClasses } from '@mui/material';
import { useEffect } from 'react';

export default function ProductPanel2() {
    const btn = document.querySelector("#showAll-btn");
    const [showAll, setShowAll] = useState(false);
    const selected = showAll ? data : data.slice(2,14);
    const selectedCards = selected.map(item => {
        return <ProductCard2 
                    key={item.name}
                    name={item.name}
                    img={item.img}
                    brand={item.brand}
                    price={item.price}
                />
    })

    useEffect(() => {
        btn.addEventListener('click', () => {
            btn.innerText = "Fetching Items...";
            btn.backgroundColor = "rgb(78, 78, 78)";
            setTimeout(() => {
                setShowAll(true);
                btn.display = "none";
            }, 1000);    
        })

        return () => {
            btn.removeEventListener('click', () => {
                btn.innerText = "Fetching Items...";
                btn.backgroundColor = "rgb(78, 78, 78)";
                setTimeout(() => {
                    setShowAll(true);
                    btn.display = "none";
                }, 1000);   
            })
        }
    })

    return (
        <div className='productPanel2'>
            <div className='productPanel2--grid'>
                {selectedCards}
            </div>
            {!showAll && <button id="showAll-btn">Show More Items</button>}
        </div>



    )
}
