import React, { useState, useContext } from 'react';
import ProductCard2 from './ProductCard2';
import "./sass/productcard.css";
import MainContext from '../context/Context';
import { useEffect } from 'react';

export default function ProductPanel2() {

    const { products, filterLocal } = useContext(MainContext);
    const cardList = localStorage.getItem('renderAllCards') === 'y' ? products : products.slice(2, 14);
    const [selected, setSelected] = useState(cardList);
    const selectedCards = selected.map(item => {
        return <ProductCard2
            key={item.id}
            id={item.id}
        />
    })

    const [filter, setFilter] = useState(JSON.parse(localStorage.getItem('filter')));
    const [filterResultFlag, setFilterResultFlag] = useState(false);


    filterResultFlag = filter.brand === 'All Brands' ? false : true;
    filterResultFlag = filter.sort === 'None' ? false : true;
    filterResultFlag = filter.priceRange[0] === 56 && filter.priceRange[1] === 674 ? false : true;


    useEffect(() => {
        setFilter(JSON.parse(localStorage.getItem('filter')));
        let filteredProducts;
        filteredProducts = filterBrand(filter.brand, products);
        filteredProducts = filterSort(filter.sort, filteredProducts);
        filteredProducts = filterPrice(filter.priceRange, filteredProducts);
        setSelected(filteredProducts);

        set

    }, [filterLocal])

    function filterBrand(brand, products) {
        if (brand === 'All Brands') {
            return products
        } else {
            return products.filter(product => product.brand === brand)
        }
    }

    function filterSort(sort, products) {
        if (sort === 'None') {
            return products
        } else if (sort === 'Name Ascending A - Z') {
            return products.sort(compareNames)
        } else if (sort === 'Name Descending Z - A') {
            return products.sort(compareNames).toReversed()
        } else if (sort === 'Price Low - High') {
            return products.sort(compareNumbers)
        } else {
            return products.sort(compareNumbers).toReversed()
        }
    }

    function filterPrice(priceRange, products) {
        return products.filter(product => product.price >= priceRange[0] && product.price <= priceRange[1])
    }


    function compareNames(a, b) {
        const nameA = a.name.toUpperCase(); // ignore upper and lowercase
        const nameB = b.name.toUpperCase(); // ignore upper and lowercase
        if (nameA < nameB) {
          return -1;
        }
        if (nameA > nameB) {
          return 1;
        }
        // names must be equal
        return 0;
    }

    function compareNumbers(a, b) {
        return a - b;
    }


    useEffect(() => {
        const btn = document.querySelector("#showAll-btn");
        btn.addEventListener('click', () => {
            btn.innerText = "Fetching Items...";
            btn.style.backgroundColor = "rgb(78, 78, 78)";
            setTimeout(() => {
                setSelected(products);
                btn.style.opacity = 0;
            }, 1000);
            localStorage.setItem('renderAllCards', 'y')
        })
    })



    return (
        <div className='productPanel2'>
            {filterResultFlag && <div className='productPanel2--filter'>
                            found {selected.length} products
                            {filter.brand !== 'All Brands' && <div><p>brand:</p>{filter.brand}<button>tbd</button></div>}
                            {filter.sort !== 'None' && <div><p>sort:</p>{filter.sort}<button>tbd</button></div>}
                            {(filter.priceRange[0] !== 56 || filter.priceRange[1] !== 674) && <div><p>price range:</p>${filter.priceRange[0]} - ${filter.priceRange[1]}<button>tbd</button></div>}
                        </div>}

            <div className='productPanel2--grid'>
                {selectedCards}
            </div>
            <button id="showAll-btn" style={{ opacity: selected.length < 18 ? 1 : 0 }}>Show More Items</button>
        </div>

    )
}
