import React, {useState, useContext } from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import MainContext from '../context/Context';
import "./sass/filter.css";

export default function Filter() {
        // get 'products' state and 'local' state
    const { products } = useContext(MainContext);

    // initialize brand select values
    const brands = [... new Set(products.map(product => product.brand))];
    brands.unshift('All Brands');

    const sortBy = [{label: 'None', value: 0}, 
                    {label: 'Name Ascending A - Z', value: 1}, 
                    {label: 'Name Descending Z - A', value: 1}, 
                    {label: 'Price High - Low', value: 1}, 
                    {label: 'Price Low - High', value: 1}]



    const { showFilter, setShowFilter } = useContext(MainContext);

    // brand select
    const [brand, setBrand] = useState('All Brands');
    const handleBrandChange = (event) => {
        setBrand(event.target.value);
    };
    const brandItemHTML = brands.map((brand, idx) => <MenuItem key={idx} value={brand}>{brand}</MenuItem>);

    const applyFilter = () => {
        setShowFilter(false)
    }

    const resetFilter = () => {
        setShowFilter(false)
    }

    return (
        <div className="filter">
            <div className="filter--selector">
                <p className='small-text'>Brand select</p>
                <FormControl sx={{ m: 1, minWidth: 120 }} className="select">
                    <InputLabel id="brand-select-label">Brand</InputLabel>
                    <Select
                        labelId="brand-select-label"
                        id="brand-select"
                        value={brand}
                        label="brand"
                        onChange={handleBrandChange}
                    >
                        {brandItemHTML}
                    </Select>
                </FormControl>
            </div>
            <div className="filter--selector">
                <p>price range</p>
                {/* price bar */}
            </div>
            <div className="filter--btn">
                <button onClick={applyFilter}>Apply filters</button>
                <button onClick={resetFilter}>Reset filters</button>
            </div>
        </div>
    )
}
