import "./sass/index.css";

function App() {
  useEffect(() => {
    const btn = document.querySelector("#showAll-btn");
    btn.addEventListener('click', () => {
        btn.innerText = "Fetching Items...";
        btn.style.backgroundColor = "rgb(78, 78, 78)";
        setTimeout(() => {
            setShowAll(true);
            btn.style.display = "none";
        }, 1000);    
    })
})

  return (
    <div className="app">
      <BrowserRouter>
        <Header />
        <Routerview />
        <Footer />
      </BrowserRouter>
    </div>
  );
}

export default App;
